import { useState, useEffect } from "react";
import classess from "./LessonForm.module.css"

function LessonForm(){
    const [dataGroup, setDataGroup] = useState(null);
    const [dataTeacher, setDataTeacher] = useState(null);
    const [dataClassroom, setDataClassroom] = useState(null);

    const [lessonId, setLessonId] = useState("");
    const [subjectName, setSubjectName] = useState("");
    const [subjectId, setSubjectId] = useState("");

    const [groupName, setGroupName] = useState([]);
    const [groupId, setGroupId] = useState([]);
    const [teacherName, setTeacherName] = useState([]);
    const [teacherId, setTeacherId] = useState([]);
    const [classroomName, setClassroomName] = useState([]);
    const [classroomId, setClassroomId] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8000/groups")
        .then(r => r.json())
        .then(d => setDataGroup(d));

        fetch("http://localhost:8000/teachers")
        .then(r => r.json())
        .then(d => setDataTeacher(d));

        fetch("http://localhost:8000/classrooms")
        .then(r => r.json())
        .then(d => setDataClassroom(d));
    }, []);

    const addGroup = (event) => {
        const val = event.target.value.split(",");
        setGroupId([...groupId, val[0]]);
        setGroupName([...groupName, val[1]]);   
    }

    const addTeacher = (event) => {
        const val = event.target.value.split(",");
        setTeacherId([...teacherId, val[0]]);
        setTeacherName([...teacherName, val[1]]);      
    }

    const addClassroom = (event) => {
        const val = event.target.value.split(",");
        setClassroomId([...classroomId, val[0]]);
        setClassroomName([...classroomName, val[1]]);      
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const gr = [];
        const te = [];
        const cl = [];

        for (let i=0; i<groupId.length; i++){
            gr[i] = {};
            gr[i]["group_id"] = groupId[i];
        }

        for (let i=0; i<teacherId.length; i++){
            te[i] = {};
            te[i]["teacher_id"] = teacherId[i];
        }
        for (let i=0; i<classroomId.length; i++){
            cl[i] = {};
            cl[i]["classroom_id"] = classroomId[i];
        }

        const newLesson = {
            "lesson": {
                "lesson_id": lessonId,
                "subject_id": subject_id,
                "subject_name": subject_name,
            },
            "groups": gr,
            "teachers": te,
            "classrooms": cl,
        }
        console.log(newLesson);
        fetch("http://localhost:8000/lessons", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(newLesson)
        })
    }

    return (
        <form onSubmit={handleSubmit}>
            <h1>SUBMIT LESSON</h1>
            <div className={classess.lesson}>
                <label>Enter lesson id: 
                    <input 
                    type="text"
                    name="lessonId"
                    value={lessonId}
                    onChange={(e) => {setLessonId(e.target.value)}}
                    />
                </label>

                <br />
                <label>Enter subject id: 
                    <input 
                    type="text"
                    name="subjectId"
                    value={subjectId}
                    onChange={(e) => {setSubjectId(e.target.value)}}
                    />
                </label>

                <br />
                <label>Enter subject name: 
                    <input 
                    type="text"
                    name="subjectName"
                    value={subjectName}
                    onChange={(e) => {setSubjectName(e.target.value)}}
                    />
                </label>
            </div>
            
            <div className={classess.lesson}>
            <br />
                <label>Chosen groups:
                    <input 
                        type="text"
                        name="groupName"
                        value={groupName.join(", ")}
                        readOnly
                    />
                </label>
                <br />
                <label>Chose next group:
                    <select id="groups" name="groups" onChange={addGroup}>
                    <option disabled selected value> -- select an option -- </option>
                    {dataGroup &&
                        dataGroup.map((item) => {
                        return (
                            <option key={item.group_id} value={[item.group_id, item.group_name]}>
                                {item.group_name}
                            </option>
                        );
                    })}
                    </select>
                </label>
            </div>
            
            <div className={classess.lesson}>
                <br />
                <br />
                <label>Chosen teachers:
                    <input 
                        type="text"
                        name="teacherName"
                        value={teacherName.join(", ")}
                        readOnly
                    />
                </label>
                <br />
                <label>Chose next teacher:
                    <select id="teachers" name="teachers" onChange={addTeacher}>
                    <option disabled selected value> -- select an option -- </option>
                    {dataTeacher &&
                        dataTeacher.map((item) => {
                        return (
                            <option key={item.teacher_id} value={[item.teacher_id, item.teacher_name]}>
                                {item.teacher_name}
                            </option>
                        );
                    })}
                    </select>
                </label>
            </div>
            
            <div className={classess.lesson}>
                <br />
                <br />
                <label>Chosen classrooms:
                    <input 
                        type="text"
                        name="classroomName"
                        value={classroomName.join(", ")}
                        readOnly
                    />
                </label>
                <br />
                <label>Chose next classroom:
                    <select id="classrooms" name="classrooms" onChange={addClassroom}>
                    <option disabled selected value> -- select an option -- </option>
                    {dataClassroom &&
                        dataClassroom.map((item) => {
                        return (
                            <option key={item.classroom_id} value={[item.classroom_id, item.classroom_name]}>
                                {item.classroom_name}
                            </option>
                        );
                    })}
                    </select>
                </label>
            </div>  
            <input type="submit" value="Submit lesson" />         
        </form>
    )
}

export default LessonForm;
