import { useState, useEffect } from "react";
import classess from "./TeacherUpdate.module.css"

function TeacherUpdate(){
    const [dataTeacher, setDataTeacher] = useState(null);
    const [queryId, setQueryId] = useState("")
    const [teacher, setTeacher] = useState(null)

    useEffect(() => {
        fetch("http://localhost:8000/teachers")
        .then(r => r.json())
        .then(d => setDataTeacher(d));
    }, []);

    const showTeacher = (event) => {
        setQueryId(event.target.value)
        for (let i=0; i<dataTeacher.length; i++){
            if (dataTeacher[i].teacher_id == event.target.value){
                setTeacher(dataTeacher[i]);
                break;
            }
        }
    }

    const handleSubmit = (event) => {
        let url = "http://localhost:8000/teachers?teacher_id=" + queryId;
        fetch(url, {
            method: "PUT",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(teacher)
        });
    }

    return (
        <form>
            <h1>TEACHER UPDATE</h1>
            <label>Choose the teacher to update: <br />
                <select id="teacher" name="teacher" onChange={showTeacher}>
                    <option disabled selected value> -- select an option -- </option>
                    {dataTeacher &&
                        dataTeacher.map(item => {
                            return <option key={item.teacher_id} value={item.teacher_id}>
                                    {item.teacher_name}
                                </option>
                        })}
                </select>
            </label>
            {teacher &&
            <>
                <h1>Chosen teacher's information:</h1>  
                <label> Name:
                    <input type="text" value={teacher.teacher_name} 
                        onChange={(e) => {setTeacher(prevState => ({
                            ...prevState,
                            "teacher_name": e.target.value
                        }))}}
                    />
                </label>
                <label> ID:
                    <input type="text" value={teacher.teacher_id} 
                        onChange={(e) => {setTeacher(prevState => ({
                            ...prevState,
                            "teacher_id": e.target.value
                        }))}}
                    />
                </label>
                <input type="submit" value="Update teacher" />
            </>
            }
        </form>
    )
}

export default TeacherUpdate;
