import { useState, useEffect } from "react";
import classess from "./LessonForm.module.css"
import LessonFormHelper from "./LessonFormHelper";

function LessonForm(){
    const [dataGroup, setDataGroup] = useState(null);
    const [dataTeacher, setDataTeacher] = useState(null);
    const [dataClassroom, setDataClassroom] = useState(null);

    const [lessonId, setLessonId] = useState("");
    const [subjectName, setSubjectName] = useState("");
    const [subjectId, setSubjectId] = useState("");
    const [nonpermittedColors, setNonpermittedColors] = useState("");

    const [groupName, setGroupName] = useState([]);
    const [groupId, setGroupId] = useState([]);
    const [teacherName, setTeacherName] = useState([]);
    const [teacherId, setTeacherId] = useState([]);
    const [classroomName, setClassroomName] = useState([]);
    const [classroomId, setClassroomId] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8000/groups")
        .then(r => r.json())
        .then(d => setDataGroup(d));

        fetch("http://localhost:8000/teachers")
        .then(r => r.json())
        .then(d => setDataTeacher(d));

        fetch("http://localhost:8000/classrooms")
        .then(r => r.json())
        .then(d => setDataClassroom(d));
    }, []);

    const addGroup = (event) => {
        const val = event.target.value.split(",");
        let needAdding = true;
        for(let i=0; i<groupId.length; i++)
        {
            if(groupId[i] === val[0]){
                needAdding = false;
                break;
            }
        }
        if (needAdding)
        {
            setGroupId([...groupId, val[0]]);
            setGroupName([...groupName, val[1]]);
        } 
        else {
            setGroupId(groupId.filter(id => id !== val[0]));
            setGroupName(groupName.filter(name => name !== val[1]));
        }
        
    }

    const addTeacher = (event) => {
        const val = event.target.value.split(",");   
        let needAdding = true;
        for(let i=0; i<teacherId.length; i++)
        {
            if(teacherId[i] === val[0]){
                needAdding = false;
                break;
            }
        }
        if (needAdding)
        {
            setTeacherId([...teacherId, val[0]]);
            setTeacherName([...teacherName, val[1]]);
        } 
        else {
            setTeacherId(teacherId.filter(id => id !== val[0]));
            setTeacherName(teacherName.filter(name => name !== val[1]));
        }
    }

    const addClassroom = (event) => {
        const val = event.target.value.split(",");    
        let needAdding = true;
        for(let i=0; i<classroomId.length; i++)
        {
            if(classroomId[i] === val[0]){
                needAdding = false;
                break;
            }
        }
        if (needAdding)
        {
            setClassroomId([...classroomId, val[0]]);
            setClassroomName([...classroomName, val[1]]);
        } 
        else {
            setClassroomId(classroomId.filter(id => id !== val[0]));
            setClassroomName(classroomName.filter(name => name !== val[1]));
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        const newLesson = {
            "lesson": {
                "lesson_id": lessonId,
                "subject_id": subjectId,
                "subject_name": subjectName,
                "color": "-1",
                "chosen_classroom": "",
                "nonpermitted_colors": nonpermittedColors,
            },
            "group_ids": groupId,
            "teacher_ids": teacherId,
            "classroom_ids": classroomId,
        };

        fetch("http://localhost:8000/lessons", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(newLesson)
        });

        setLessonId("");
        setSubjectId("");
        setSubjectName("");
        setNonpermittedColors("");

        setGroupId([]);
        setGroupName([]);
        setTeacherId([]);
        setTeacherName([]);
        setClassroomId([]);
        setClassroomName([]);
    }

    return (
        <form onSubmit={handleSubmit}>
            <h1>SUBMIT LESSON</h1>
            <div className={classess.lesson}>
                <label>Enter lesson id: 
                    <input 
                    type="text"
                    name="lessonId"
                    value={lessonId}
                    onChange={(e) => {setLessonId(e.target.value)}}
                    />
                </label>

                <br />
                <label>Enter subject id: 
                    <input 
                    type="text"
                    name="subjectId"
                    value={subjectId}
                    onChange={(e) => {setSubjectId(e.target.value)}}
                    />
                </label>

                <br />
                <label>Enter subject name: 
                    <input 
                    type="text"
                    name="subjectName"
                    value={subjectName}
                    onChange={(e) => {setSubjectName(e.target.value)}}
                    />
                </label>

                <br />
                <label>Enter nonpermitted intervals: 
                    <input 
                    type="text"
                    name="nonpermittedColors"
                    value={nonpermittedColors}
                    onChange={(e) => {setNonpermittedColors(e.target.value)}}
                    />
                </label>
            </div>
            
            <LessonFormHelper 
                name="group" 
                itemId={groupId} 
                setItemId={setGroupId}
                itemName={groupName}
                setItemName={setGroupName}
                data={dataGroup}
            />

            <LessonFormHelper 
                name="teacher" 
                itemId={teacherId} 
                setItemId={setTeacherId}
                itemName={teacherName}
                setItemName={setTeacherName}
                data={dataTeacher}
            />
            
            <LessonFormHelper 
                name="classroom" 
                itemId={classroomId} 
                setItemId={setClassroomId}
                itemName={classroomName}
                setItemName={setClassroomName}
                data={dataClassroom}
            />
            
            <input type="submit" value="Submit lesson" />         
        </form>
    )
}

export default LessonForm;
