import { useState, useEffect, useRef } from "react";
import classess from "./UpdateForm.module.css"

function UpdateForm(props){
    const [data, setData] = useState(null);
    const [queryId, setQueryId] = useState("");
    const [item, setItem] = useState(null);
    const todo = useRef("");
    const url = "http://localhost:8000/" + props.name + "s";
    const itemAttributeName = props.name + "_name";
    const itemAttributeId = props.name + "_id";
    const valueSubmitButton = "SUBMIT " + props.name.toUpperCase();

    useEffect(() => {
        fetch(url)
        .then(r => r.json())
        .then(d => setData(d));
        setItem(null);
        setQueryId("");
    }, [props.name, url]);

    const showItem = (event) => {
        setQueryId(event.target.value)
        for (let i=0; i<data.length; i++){
            if (data[i][itemAttributeId] === event.target.value){
                setItem(data[i]);
                break;
            }
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        let urlQuery = "http://localhost:8000/" + props.name + "s?" + itemAttributeId + "=" + queryId;
        if (todo.current === "update")
        {
            fetch(urlQuery, {
            method: "PUT",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(item)
            }).then(() => fetch(url))
            .then(r => r.json())
            .then(d => setData(d));
        }
        else if (todo.current === "delete")
        {
            fetch(urlQuery, {
                method: "DELETE",
            }).then(() => fetch(url))
            .then(r => r.json())
            .then(d => setData(d));
        }

        setItem(null);
        setQueryId("");
        todo.current = "";
        
    }

    return (
        <form onSubmit={handleSubmit}>
            <h1>{props.name.toUpperCase()} UPDATE</h1>
            <label>Choose the {props.name} to update: <br />
                <select id={props.name} name={props.name}>
                    <option disabled selected value> -- select an option -- </option>
                    {data &&
                        data.map(elem => {
                            return <option key={elem[itemAttributeId]} value={elem[itemAttributeId]} onClick={showItem}>
                                    {elem[itemAttributeName]}
                                </option>
                        })}
                </select>
            </label>
            {item &&
            <>
                <h1>Chosen {props.name}'s information:</h1>  
                <label> Name:
                    <input type="text" value={item[itemAttributeName]} 
                        onChange={(e) => {setItem(prevState => ({
                            ...prevState,
                            [itemAttributeName]: e.target.value
                        }))}}
                    />
                </label>
                <label> ID:
                    <input type="text" value={item[itemAttributeId]} 
                        onChange={(e) => {setItem(prevState => ({
                            ...prevState,
                            [itemAttributeId]: e.target.value
                        }))}}
                    />
                </label>
                <input type="submit" value={valueSubmitButton} onClick={() => todo.current = "update"}/>
                <input type="submit" value={valueSubmitButton} onClick={() => todo.current = "delete"}/>
            </>
            }
        </form>
    )
}

export default UpdateForm;
