import { useState, useEffect } from "react";
import FetchData from "./FetchData";

function LessonForm(){
    const [lessonId, setLessonId] = useState("");
    const [subjectName, setSubjectName] = useState("");
    const [subjectId, setSubjectId] = useState("");

    const [groupName, setGroupName] = useState([]);
    const [groupId, setGroupId] = useState([]);

    

    const addGroup = (event) => {
        setGroupId([...groupId, event.target.key]);
        setGroupName([...groupName, event.target.value]);
        
    }

    return (
        <form>
            <h1>SUBMIT LESSON</h1>
            <label>Enter lesson id: 
                <input 
                type="text"
                name="lessonId"
                value={lessonId}
                onChange={(e) => {setLessonId(e.target.value)}}
                />
            </label>
            <br />
            <label>Enter subject id: 
                <input 
                type="text"
                name="subjectId"
                value={subjectId}
                onChange={(e) => {setSubjectId(e.target.value)}}
                />
            </label>
            <br />
            <label>Enter subject name: 
                <input 
                type="text"
                name="subjectName"
                value={subjectName}
                onChange={(e) => {setSubjectName(e.target.value)}}
                />
            </label>
            <br />
            <label>Chosen groups:
                <input 
                    type="text"
                    name="groupName"
                    value={groupName.join(", ")}
                    readOnly
                />
            </label>
            <br />
            <label>Chose next group:
                <select id="groups" name="groups" onChange={addGroup}>
                {data &&
                    data.map((item) => {
                    return (
                        <option key={item.group_id} value={item.group_name}>
                            {item.group_name}
                        </option>
                    );
                })}
                </select>
            </label>
        </form>
    )
}

export default LessonForm;
