import { useState, useEffect } from "react";
import parse from 'html-react-parser';
import classes from "./ReportLesson.module.css";

function ReportLesson(props){
    const [data, setData] = useState(null);
    const url = "http://localhost:8000/lessons";
    useEffect(() => {
        fetch(url)
        .then(r => r.json())
        .then(d => setData(d));
    }, [url]);

    if(data){
        var table = "<table>";
        for(let i=0; i<data.length; i++)
        {
            if (i%5 === 0){
                table += "<tr>";
            }
            table += "<td><table>";
            for (let j=0; j<data[i].length; j++){
                if(i%2 === 0){
                    if(j%2 === 0){
                        table += "<tr><td style='background-color:yellow'>" + data[i][j]["lesson_id"] + "</td></tr>"; 
                    }
                    else {
                        table += "<tr><td style='background-color:blue'>" + data[i][j]["lesson_id"] + "</td></tr>"; 
                    }
                }
                else {
                    table += "<tr><td>" + data[i][j]["lesson_id"] + "</td></tr>";
                }
            }
            table += "</table></td>";
            if(i%5 === 4){
                table += "</tr>";
            }
        }
        table += "</table>";
        return <>{parse(table)}</>;
    }
    return <h1> Wait for a moment</h1>
}

export default ReportLesson;
