import { useState, useEffect } from "react";
import parse from 'html-react-parser';
import classes from "./ReportLesson.module.css";

function ReportLesson(props){
    const [data, setData] = useState(null);
    const url = "http://localhost:8000/lessons";
    useEffect(() => {
        fetch(url)
        .then(r => r.json())
        .then(d => setData(d));
    }, [url]);

    if(data){
        data_new = [];
        var row=-1;
        for(let i=0; i<data.length; i++)
        {
            if(i%5 === 0){
                row ++;
                data_new[row] = [];
            }
            data_new[row].push(data[i]);
        }
        return (
            <table>
                {(data_new) => {
                    for(let i=0; i<data_new.length; i++){
                        return (
                            <tr>
                                {(item_row) => {
                                    for(let j=0; j<item_row.length; j++){
                                        return(
                                            <td>
                                                <table>
                                                    {
                                                        item_row[j].map((item) => {
                                                            <tr><td>
                                                                {item["lesson_id"]}
                                                            </td></tr>
                                                        })
                                                    }
                                                </table>
                                            </td>
                                        )
                                    }
                                }}
                            </tr>
                        )
                    }
                }}
            </table>
        )
        var table = "<table>";
        for(let i=0; i<data.length; i++)
        {
            if (i%5 === 0){
                table += "<tr>";
            }
            if (i%2 === 0) {
                table += "<td style='background-color:yellow'><table>";
            }
            else {
                table += "<td style='background-color:lightblue'><table>";
            }
            
            for (let j=0; j<data[i].length; j++){
                table += "<tr><td>"
                table += "<strong>" + data[i][j]["subject_name"] + "</strong>";
                table += "<br/><strong>" + data[i][j]["topic_name"] + "</strong>";
                table += "<br/><strong>" + data[i][j]["color"] + "</strong>";
                if(data[i][j]["teachers"].length > 0)
                {
                    table += "<br/><strong>Teachers:</strong><br/>";
                    table += data[i][j]["teachers"][0];
                    for (let k=1; k < data[i][j]["teachers"].length; k++){
                        table += ", " + data[i][j]["teachers"][k];
                    }
                }

                if(data[i][j]["groups"].length > 0)
                {
                    table += "<br/><strong>Groups:</strong><br/>";
                    table += data[i][j]["groups"][0];
                    for (let k=1; k < data[i][j]["groups"].length; k++){
                        table += ", " + data[i][j]["groups"][k];
                    }
                }

                table += "<br/><strong>Classroom:</strong><br/>";
                table += data[i][j]["chosen_classroom"];
                table += "</td></tr>";
            }
            table += "</table></td>";
            if(i%5 === 4){
                table += "</tr>";
            }
        }
        table += "</table>";
        return <>{parse(table)}</>;
    }
    return <h1> Wait for a moment</h1>
}

export default ReportLesson;
