import classess from "./LessonForm.module.css"

function LessonFormHelper(props){
    iId = props.name + "_id";
    iName = props.name + "_name";
    const addItem = (event) => {
        const val = event.target.value.split(",");
        let needAdding = true;
        for(let i=0; i<props.itemId.length; i++)
        {
            if(props.itemId[i] === val[0]){
                needAdding = false;
                break;
            }
        }
        if (needAdding)
        {
            setItemId([...itemId, val[0]]);
            setItemName([...itemName, val[1]]);
        } 
        else {
            setItemId(itemId.filter(id => id !== val[0]));
            setItemName(itemName.filter(name => name !== val[1]));
        }
    }

    <div className={classess.lesson}>
            <br />
                <label>Chosen {props.name}s:
                    <input 
                        type="text"
                        name={props.name+"Name"}
                        value={itemName.join(", ")}
                        readOnly
                    />
                </label>
                <br />
                <label>Chose next {props.name}:
                    <select id={props.name} name={props.name} onChange={addItem} value={"default"}>
                    <option disabled value="default"> -- select an option -- </option>
                    {props.data &&
                        props.data.map((item) => {
                        return (
                            <option key={item[iId]} value={[item[iId], item[iName]]}>
                                {item[iName]}
                            </option>
                        );
                    })}
                    </select>
                </label>
            </div>
}

export default LessonFormHelper;
