import { useState, useEffect } from "react";

function LessonForm(){
    const [dataGroup, setDataGroup] = useState(null);
    const [dataTeacher, setDataTeacher] = useState(null);

    const [lessonId, setLessonId] = useState("");
    const [subjectName, setSubjectName] = useState("");
    const [subjectId, setSubjectId] = useState("");

    const [groupName, setGroupName] = useState([]);
    const [groupId, setGroupId] = useState([]);
    const [teacherName, setTeacherName] = useState([]);
    const [teacherId, setTeacherId] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8000/groups")
        .then(r => r.json())
        .then(d => setDataGroup(d));

        fetch("http://localhost:8000/teachers")
        .then(r => r.json())
        .then(d => setDataTeacher(d));
    }, []);

    const addGroup = (event) => {
        setGroupId([...groupId, event.target.key]);
        setGroupName([...groupName, event.target.value]);
        
    }

    const addTeacher = (event) => {
        const val = event.target.value.split(",");
        setTeacherId([...teacherId, val[0]]);
        setTeacherName([...teacherName, event.target.value]);
        
    }

    return (
        <form>
            <h1>SUBMIT LESSON</h1>
            <label>Enter lesson id: 
                <input 
                type="text"
                name="lessonId"
                value={lessonId}
                onChange={(e) => {setLessonId(e.target.value)}}
                />
            </label>
            <br />
            <label>Enter subject id: 
                <input 
                type="text"
                name="subjectId"
                value={subjectId}
                onChange={(e) => {setSubjectId(e.target.value)}}
                />
            </label>
            <br />
            <label>Enter subject name: 
                <input 
                type="text"
                name="subjectName"
                value={subjectName}
                onChange={(e) => {setSubjectName(e.target.value)}}
                />
            </label>
            <br />
            <label>Chosen groups:
                <input 
                    type="text"
                    name="groupName"
                    value={groupName.join(", ")}
                    readOnly
                />
            </label>
            <br />
            <label>Chose next group:
                <select id="groups" name="groups" onChange={addGroup}>
                {dataGroup &&
                    dataGroup.map((item) => {
                    return (
                        <option key={item.group_id} value={item.group_name}>
                            {item.group_name}
                        </option>
                    );
                })}
                </select>
            </label>
            
            <br />
            <br />
            <label>Chosen teachers:
                <input 
                    type="text"
                    name="teacherName"
                    value={teacherId.join(", ")}
                    readOnly
                />
            </label>
            <br />
            <label>Chose next teacher:
                <select id="teachers" name="teachers" onChange={addTeacher}>
                {dataTeacher &&
                    dataTeacher.map((item) => {
                    return (
                        <option key={item.teacher_id} value={[item.teacher_id, item.teacher_name]}>
                            {item.teacher_name}
                        </option>
                    );
                })}
                </select>
            </label>
        </form>
    )
}

export default LessonForm;
