import { useState } from "react";
import reactDom from "react-dom";
import classes from "./ClassroomForm.module.css"

function ClassroomForm(){
    const [classroomName, setClassroomName] = useState("");
    const [classroomId, setClassroomId] = useState("");

    const handleSubmit = (event) => {
        event.preventDefault();
        const newClassroom = {
            "classroom_id": classroomId,
            "classroom_name": classroomName
        }

        fetch("http://localhost:8000/classrooms", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(newClassroom)
        })
        setClassroomId("");
        setClassroomName("");
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <h1 className={classes.title}>SUBMIT CLASSROOM</h1>
                <label>Enter classroom name: 
                    <input 
                    type="text"
                    name="ClassroomName"
                    value={classroomName}
                    onChange={(e) => {setClassroomName(e.target.value)}}
                    />
                </label>
                <br />
                <label>Enter classroom ID: 
                    <input 
                    type="text"
                    name="ClassroomId"
                    value={classroomId}
                    onChange={(e) => {setClassroomId(e.target.value)}}
                    />
                </label>
                <br />
                <input type="submit" value="Submit classroom"/>
            </form>
        </div>
    )
}

export default ClassroomForm;
