import { useState, useEffect } from "react";
import classess from "./TeacherUpdate.module.css"

function TeacherUpdate(){
    const [dataTeacher, setDataTeacher] = useState(null);
    const [teacher, setTeacher] = useState({})

    useEffect(() => {
        fetch("http://localhost:8000/teachers")
        .then(r => r.json())
        .then(d => setDataTeacher(d));
    }, []);

    const showTeacher = (event) => {
        for (i=0; i<length(dataTeacher); i++){
            if (dataTeacher[i].teacher_id == event.target.value){
                setTeacher(dataTeacher[i]);
                break;
            }
        }
    }

    return (
        <form>
            <h1>TEACHER UPDATE</h1>
            <label>Choose the teacher to update: <br />
                <select id="teacher" name="teacher">
                    <option disabled selected value> -- select an option -- </option>
                    {dataTeacher &&
                        dataTeacher.map(item => {
                            return <option key={item.teacher_id} value={item.teacher_id}>
                                    {item.teacher_name}
                                </option>
                        })}
                </select>
            </label>
        </form>
    )
}

export default TeacherUpdate;
