import { useState, useEffect } from "react";
import classes from "./ClassroomReport.module.css";

function ClassroomReport(){
    const [data, setData] = useState(null);

    useEffect(() => {
        fetch("http://localhost:8000/classrooms")
        .then(r => r.json())
        .then(d => setData(d));
    }, []);

    if (data){
        return (
            <table>
                <tr>
                    <th>Classroom ID</th>
                    <th>Classroom Name</th>
                </tr>
                {data &&
                    data.map((item) => {
                    return (
                        <tr key={item.classroom_id}>
                            <p key={item.id}>{item.classroom_id}</p>
                        </tr>
                    );
                    })}
            </table>
        )
    }
    return <h1> Wait for a moment</h1>
    
}


  export default ClassroomReport
