import { useState, useEffect } from "react";
import parse from 'html-react-parser';
import classes from "./ReportLesson.module.css";

function ReportLesson(props){
    const [data, setData] = useState(null);
    const url = "http://localhost:8000/lessons";
    useEffect(() => {
        fetch(url)
        .then(r => r.json())
        .then(d => setData(d));
    }, [url]);

    if(data){
        var table = "<table>";
        for(let i=0; i<data.length; i++)
        {
            if (i%5 === 0){
                table += "<tr>";
            }
            if (i%2 === 0) {
                table += "<td style='background-color:yellow'><table>";
            }
            else {
                table += "<td style='background-color:lightblue'><table>";
            }
            for (let j=0; j<data[i].length; j++){
                    table += "<tr><td>"
                    table += "<strong>" + data[i][j]["subject_name"] + "</strong><br/>";
                    for (let k=0; k < data[i][j]["teachers"].length; k++){
                        let name_arr = data[i][j]["teachers"][k].split(',');
                        table += name_arr[0] + ", ";
                    }
                    table += "</td></tr>";
            }
            table += "</table></td>";
            if(i%5 === 4){
                table += "</tr>";
            }
        }
        table += "</table>";
        return <>{parse(table)}</>;
    }
    return <h1> Wait for a moment</h1>
}

export default ReportLesson;
