import { useState } from "react";
import classes from "./SubmitForm.module.css"

function SubmitForm(props){
    const [ itemName, setItemName] = useState("");
    const [ itemId, setItemId] = useState("");
    const submitButtonValue = "SUNMI " + props.name;

    const handleSubmit = (event) => {
        event.preventDefault();
        const itemAttributeId = props.name + "_id";
        const itemAttributeName = props.name + "_name";
        const url = "http://localhost:8000/" + props.name + "s";

        const newItem = {};
        newItem[itemAttributeId] = itemId;
        newItem[itemAttributeName] = itemName;
        
        console.log(newItem);
        fetch(url, {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(newItem)
        });
        setItemName("");
        setItemId("");
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <h1 className={classes.title}>SUBMIT {props.name.toUpperCase()}</h1>
                <label>Enter {props.name} name: 
                    <input 
                    type="text"
                    name="itemName"
                    value={itemName}
                    onChange={(e) => {setItemName(e.target.value)}}
                    />
                </label>
                <br />
                <label>Enter {props.name} ID: 
                    <input 
                    type="text"
                    name="itemId"
                    value={itemId}
                    onChange={(e) => {setItemId(e.target.value)}}
                    />
                </label>
                <br />
                <input type="submit" value={submitButtonValue}/>
            </form>
        </div>
    )
}

export default SubmitForm;
