import { useState, useEffect } from "react";
import classes from "./ReportLesson.module.css";

function ReportLesson(props){
    const [data, setData] = useState(null);
    const url = "http://localhost:8000/lessons";
    useEffect(() => {
        fetch(url)
        .then(r => r.json())
        .then(d => setData(d));
    }, [url]);

    if(data){
        var table = "<table>";
        for(let i=0; i<100; i++)
        {
            if (i%5 ==0){
                table += "<tr>";
            }
            table += "<table>";
            for (let j=0; j<data[i].length(); j++){
                table += "<tr>" + data[i][j]["lesson_id"] + "</tr>";
            }
            table += "</table";
            if(i%5 == 4){
                table += "</tr>";
            }
        }
        table += "</table>";
        return table;
    }
    return <h1> Wait for a moment</h1>
}

export default ReportLesson;
